// components/UserManagementPage.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import UserService from '../service/UserService';

function VideoManagementPage() {
    const [videos, setVideos] = useState([]);

    useEffect(() => {
        // Fetch videos data when the component mounts
        fetchVideos();
    }, []);

    const fetchVideos = async () => {
        try {

            const token = localStorage.getItem('token'); // Retrieve the token from localStorage
            const response = await UserService.getAllVideos(token);
            console.log(response);
            setVideos(response.videoList); // Assuming the list of videos is under the key 'ourVideosList'
        } catch (error) {
            console.error('Error fetching videos:', error);
        }
    };

    const assignVideo = async (videoId, userId) => {
        try {
            // Prompt for confirmation before assign the user
            const confirmAssign = window.confirm('Are you sure you want to assign this video?');

            const token = localStorage.getItem('token'); // Retrieve the token from localStorage
            if (confirmAssign) {
                await UserService.assignVideo(videoId, userId, token);
                // After assigning the user, fetch the updated list of videos
                await fetchVideos();
            }
        } catch (error) {
            console.error('Error assigning video:', error);
        }
    };

    return (
        <div className="video-management-container">
            <h2>Videos Management Page</h2>
            <button className='reg-button'> <Link to="/register">Add Video</Link></button>
            <table>
                <thead>
                <tr>
                    <th>ID</th>
                    <th>Title</th>
                    <th>FileId</th>
                    <th>AssignedUser</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {videos
                    .map(video => (
                        <tr key={video.id}>
                            <td>{video.id}</td>
                            <td>{video.title}</td>
                            <td>{video.fileId}</td>
                            <td>{video.assignedUser}</td>
                            <td>
                                <button>
                                    <Link to={`/update-user/${video.id}`}>
                                        Assign
                                    </Link>
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default VideoManagementPage;
