// components/UserManagementPage.js
import React, { useState, useEffect } from 'react';
import UserService from '../service/UserService';
import VideoUploadModal from "./VideoUploadModal";
import VideoAssignModal from "./VideoAssignModal";

function VideoManagementPage() {
    const [videos, setVideos] = useState([]);

    const [videoId, setVideoId] = useState('');

    const [isModalOpen, setIsModalOpen] = useState(false);

    const [isAssignModalOpen, setAssignModalOpen] = useState(false);
    const [isUploadModalOpen, setUploadModalOpen] = useState(false);

    const openAssignModal = (videoId) => {
        setVideoId(videoId);
        setAssignModalOpen(true);
    };

    const closeAssignModal = () => {
        setAssignModalOpen(false);
        setVideoId(null);
    };

    const openUploadModal = () => {
        setUploadModalOpen(true);
    };

    const closeUploadModal = () => {
        setUploadModalOpen(false);
    };

    // const openModal = () => setIsModalOpen(true);
    // const closeModal = () => setIsModalOpen(false);
    // const openVideoAssignModal = (id) => {
    //     setIsModalOpen(true);
    //     setVideoId(id);
    // };

    useEffect(() => {
        // Fetch videos data when the component mounts
        fetchVideos();
    }, []);

    const fetchVideos = async () => {
        try {
            const token = localStorage.getItem('token'); // Retrieve the token from localStorage
            const response = await UserService.getAllVideos(token);
            console.log(response);
            setVideos(response.videoList); // Assuming the list of videos is under the key 'ourVideosList'
        } catch (error) {
            console.error('Error fetching videos:', error);
        }
    };

    return (
        <div className="video-management-container">
            <h2>Videos Management Page</h2>
            <button className='reg-button' onClick={openModal}>Upload Video</button>
            <table>
                <thead>
                <tr>
                    <th>ID</th>
                    <th>Title</th>
                    <th>FileId</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {videos
                    .map(video => (
                        <tr key={video.id}>
                            <td>{video.id}</td>
                            <td>{video.title}</td>
                            <td>{video.fileId}</td>
                            <td>
                                <button className='reg-button' onClick={()=> {openVideoAssignModal(video.id)}}>
                                    Assign
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <VideoUploadModal isOpen={isModalOpen} onClose={closeModal}/>
            <VideoAssignModal id={videoId} isOpen={isModalOpen} onClose={closeModal}/>
        </div>
    );
}

export default VideoManagementPage;
