import React, {useState} from 'react';
import UserService from "../service/UserService";

function VideoPlaybackModal({ isOpen, onClose, videoId, fileId }) {
    const [message, setMessage] = useState("");
    const videoUrl = `http://localhost:8005/public/${fileId}`;

    const completeVideo = async (userId) => {
        try {
            // Prompt for confirmation before assign the user
            const confirmComplete = window.confirm('Are you sure you want to confirm complete this video?');

            const token = localStorage.getItem('token'); // Retrieve the token from localStorage
            if (confirmComplete) {
                await UserService.getCompleteVideo(videoId, token);
                // After assigning the user, fetch the updated list of videos
                setMessage("Video assigned successfully.");
            }
        } catch (error) {
            console.error('Error assigning video:', error);
        }
    };

    if (!isOpen) return null;

    return (
        <div className="modal">
            <div className="modal-content">
                {message && <p>{message}</p>}
                <video width="600" controls>
                    <source src={videoUrl} type="video/mp4"/>
